function getSource($pageID)
{
        //$url = "http://en.wikipedia.org/wiki/Special:Export/$pageID";
        $url = "http://wiking.vulcan.com/wp/index.php/Special:Export/$pageID";

		//echo $url; die;
		$context = stream_context_create(
                array(
                        "http" => array(
                                "user_agent" => "DBpedia"
                        )
                ));
		//echo $context;die;
        $xml = simplexml_load_string(file_get_contents($url, false, $context));
		//echo  $xml->page->revision->text;die;

        if(!$xml->page || !$xml->page->revision)
                return null;

        return $xml->page->revision->text;
    }
